    private void AddMovieActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_AddMovieActionPerformed
        if(!MovieName.getText().isBlank()){
            BD bd = new BD();
            String sql = "insert into peliculas values('" + MovieName.getText() + "', '" + MovieDescription.getText() + "'," + MovieCheck.isSelected() + ");";
            bd.sentencia(sql);
            dispose();
        }else JOptionPane.showMessageDialog(rootPane, "Es necesario un nombre.");
    }//GEN-LAST:event_AddMovieActionPerformed

    private void AddSeriesActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_AddSeriesActionPerformed
        if(!SeriesName.getText().isBlank()){
            BD bd = new BD();
            String sql = "insert into series values('" + SeriesName.getText() + "', '" + SeriesDescription.getText() + "');";
            bd.sentencia(sql);
            dispose();
        }else JOptionPane.showMessageDialog(rootPane, "Es necesario un nombre.");
    }//GEN-LAST:event_AddSeriesActionPerformed

    private void AddChapterActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_AddChapterActionPerformed
        if(SeriesTabla.getSelectedRow() != -1){
            String serie = String.valueOf(SeriesTabla.getValueAt(SeriesTabla.getSelectedRow(), 0));
            BD bd = new BD();
            String sql = "insert into capitulos (capitulo, temporada, serie, conocida) values(" + ChapterNumber.getValue() + ", " + ChapterSeason.getValue() + ", '" + serie + "', " + ChapterCheck.isSelected() + ");";
            try{
                bd.sentencia(sql);
            }catch(Exception ex){
                JOptionPane.showMessageDialog(rootPane, "Error! " + ex.getMessage() + "\n" + ex.getCause());
            }
            dispose();
        }
    }//GEN-LAST:event_AddChapterActionPerformed

