    private void mostrarSeries(){
        BD bd = new BD();
        DefaultTableModel tm = (DefaultTableModel) SeriesTabla.getModel();
        SeriesTabla.setSelectionMode(ListSelectionModel.SINGLE_SELECTION);
        
        tm.setRowCount(0);
        
        try {
            ResultSet rs1 = bd.consulta("select nombre from series where nombre like '%" + ChapterSearchBar.getText() + "%';");
            while (rs1.next()) {
                String nombre = rs1.getString("nombre"); 
                Object nuev[] = {nombre};
                tm.addRow(nuev);
            }
            bd.cerrarConexion();
        } catch (SQLException ex) {
            System.out.println("Error: " + ex.getMessage() + "\n" + ex.getCause());
        }
    }
    
